以react项目为例 Step1: 配置Vite插件 - vite.config.ts import reactRefresh from '@vitejs/plugin-react-refresh';
import { defineConfig } from 'vite';
import ViteRsw from 'vite-plugin-rsw'; export default defineConfig({ plugins: [ reactRefresh(), // 查看更多：https://github.com/lencx/vite-plugin-rsw ViteRsw({ // 支持开发(dev)和生产模式(release) // 生产模式会对wasm文件的体积进行优化 mode: "release", // 如果包在`unLinks`和`crates`都配置过 // 会执行，先卸载(npm unlink)，再安装(npm link) // 例如下面会执行 // `npm unlink wasm-hey rsw-test` unLinks: ['wasm-hey', 'rsw-test'], // 项目根路径下的rust项目 // `@`开头的为npm组织 // 例如下面会执行: // `npm link wasm-hey @rsw/hey` // 因为执行顺序原因，虽然上面的unLinks会把`wasm-hey`卸载 // 但是这里会重新进行安装 crates: ["wasm-hey", "@rsw/hey"], }), ],
}) Step2: 配置Rust项目清单 - wasm-hey/Cargo.toml # ... # https://github.com/rustwasm/wasm-pack/issues/886
# https://developers.google.com/web/updates/2019/02/hotpath-with-wasm
[package.metadata.wasm-pack.profile.release]
wasm-opt = false # See more keys and their definitions at https://doc.rust-lang.org/cargo/reference/manifest.html
[lib]
crate-type = ["cdylib", "rlib"] [profile.release]
opt-level = "s" [dependencies]
wasm-bindgen = "0.2.70" Step3: 添加Rust代码 - wasm-hey/src/lib.rs use wasm_bindgen::prelude::*; // Import the `window.alert` function from the Web.
#[wasm_bindgen]
extern "C" { fn alert(s: &str);
} // Export a `greet` function from Rust to JavaScript, that alerts a hello message.
#[wasm_bindgen]
pub fn greet(name: &str) { alert(&format!("Hello, {}!", name));
} Step4: React项目中调用Wasm方法 - src/App.tsx import React, { useEffect } from 'react';
import init, { greet } from 'wasm-hey'; import logo from './logo.svg';
import './App.css'; function App() { useEffect(() => { // wasm初始化，在调用`wasm-hey`包方法时 // 必须先保证已经进行过初始化，否则会报错 // 如果存在多个wasm包，则必须对每一个wasm包进行初始化 init(); }, []) const handleHey = () => { // 调用greet方法 greet('wasm'); } return ( <div className="App"> <header className="App-header"> <img src={logo} className="App-logo" alt="logo" /> <p>Hello WebAssembly!</p> <p>Vite + Rust + React</p> <p> <button onClick={handleHey}>hi wasm</button> </p> <p>Edit <code>App.tsx</code> and save to test HMR updates.</p> </header> </div> )
} export default App

